package com.zbw.basic.arithmetic;

public class Test4 {

	/**++ --
	 * @author zbw
	 * 2017-11-10
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num1=5;
		int num2=5;
		int num3=5;
		int num4=5;
		System.out.println(num1++);
		System.out.println(++num2);
		System.out.println(num3--);
		System.out.println(--num4);
		
		
		
		
		
	}

}
